import React from 'react';
import { Field } from 'formik';
import { Input, FormControl, FormLabel, FormHelperText } from '@mui/material';

function Textarea(props) {
    const { label, name, ...rest } = props;
    return (
        <Field name={name}>
            {
                ({field, form}) => {
                    console.log(!(form.errors[name] && form.touched[name]));
                    return <FormControl error={form.errors[name] && form.touched[name]}>
                        <FormLabel htmlFor={name}>{label}</FormLabel>
                        <Input 
                            id={name} 
                            {...rest} 
                            {...field} 
                            multiline
                            rows={4}
                        />
                        {
                            form.errors[name] && form.touched[name] ? (
                                <FormHelperText error >{form.errors[name]}</FormHelperText>
                            ) : null
                        } 

                    </FormControl>
                }
            }
        </Field>
    );
}

export default Textarea;
