import React, {useState, useEffect} from 'react';
import { CircularProgress } from '@mui/material';
import utils from '../../utils/manage/recordsUtils';
import ViewFieldComp from '../../components/ViewFieldComp/ViewFieldComp';

function RecordsDataField(props) {

    const [recordsCount, setRecordsCount] = useState(null);

    useEffect(()=>{
        let isMounted = true;

        const getData = async ()=>{
            return await utils.getRecordsCount(props.systemId);
        };

        getData().then(
            data =>{
                if (isMounted) {
                    setRecordsCount(data);
                }
            }
        );
        return () => { isMounted = false };
    },[props.systemId]);

    return (
        <ViewFieldComp title='מספר רשומות :'>
            {
                recordsCount !== null ? 
                    recordsCount 
                    : 
                    <CircularProgress color='info' size={15}/>
            }
        </ViewFieldComp>
    );
}

export default RecordsDataField;
