import React, {useState, useEffect} from 'react';
import SimpleTableComp from '../../components/SimpleTableComp';

function CallsHistory() {
    const [calls, setCalls] = useState([]);
    const [loading, setLoading] = useState(false);
    const [pageNumber, setPageNumber] = useState(1);
    const [totalPageCount, setTotalPageCount] = useState(0);

    useEffect(() => {
        let isMounted = true;

        const getData = async () => {
            setLoading(true);
            return await utils.getCompaniesPaginated(pageNumber, 15);
        };

        getData().then(
            data => {
                if (isMounted) {
                    setLoading(false);
                    setCalls(data.results);
                    setTotalPageCount(data.total);
                }
            }
        );
        return () => { isMounted = false };
    }, [pageNumber]);

    const columns = [
        {
            accessor: 'date',
            header: 'תאריך',
            width: '130px',
        },
        {
            accessor: 'from',
            header: 'מאת',
            width: '130px',
        },
        {
            accessor: 'to',
            header: 'עבור',
            width: '130px',
        },
        {
            accessor: 'direction',
            header: 'כיוון שיחה',
            width: '50px',
            renderCell: (row) => {
                return '<SettingsModal {...row}/>'
            }
        },

    ]

    return (
        <div>
            CallsHistory
            <SimpleTableComp 
                loading={loading}
                columns={columns}
                rows={calls}
            />
        </div>
    );
}

export default CallsHistory;
