import { Grid } from '@mui/material';
import LoadDataModal from './LoadDataModal';
import React, {useState, useEffect} from 'react';
import { CircularProgress } from '@mui/material';
import utils from '../../utils/manage/recordsUtils';

function RecordsCount(props) {

    const [recordsCount, setRecordsCount] = useState(null);

    useEffect(()=>{
        let isMounted = true;

        const getData = async ()=>{
            return await utils.getRecordsCount(props.systemId);
        };

        getData().then(
            data =>{
                if (isMounted) {
                    setRecordsCount(data);
                }
            }
        );
        return () => { isMounted = false };
    },[props.systemId]);

    return (
        <>
            {
                recordsCount ? recordsCount : <CircularProgress />
            }
        </>
    );
}

export default RecordsCount;
