import { Grid } from '@mui/material';
import LoadDataModal from './LoadDataModal';
import React, {useState, useEffect} from 'react';
import utils from '../../utils/manage/recordsUtils';

function RecordsDataField(props) {

    const [recordsCount, setRecordsCount] = useState(0);

    useEffect(()=>{
        let isMounted = true;

        const getData = async ()=>{
            return await utils.getRecordsCount(props.systemId);
        };

        getData().then(
            data =>{
                if (isMounted) {
                    setRecordsCount(data);
                }
            }
        );
        return () => { isMounted = false };
    },[props.systemId]);

    return (
        <Grid container>
            <Grid item xs={3}>
                {'מספר רשומות : ' + recordsCount}
            </Grid>
            <Grid item xs={6}>
                <LoadDataModal systemId={props.systemId}/>
            </Grid>
        </Grid>
    );
}

export default RecordsDataField;
