import React, {useState} from 'react';
import { Fab } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import AddNewSystemForm from './AddNewSystemForm';
import ModalTemplate from '../../components/ModalComp';;

function AddNewSystemModal(props) {
    const [open, setOpen] = useState(false);

    const title = 'הוספת מערכת';
    const fabStyle = {
        margin: 0,
        top: 'auto',
        right: 'auto',
        bottom: 20,
        left: 20,
        position: 'fixed',
    };

    const makeTrigerBtn = (additionalProps) => {
        return (
            <Fab onClick={additionalProps.onClick} style={fabStyle} title='הוסף מערכת' color='info'>
                <AddIcon/>
            </Fab>
        )
    }

    const body = (
        <div>
            <AddNewSystemForm closeModal={()=>setOpen(false)}/>
        </div>
    )

    return (
        <div>
            <ModalTemplate 
                closeBtn 
                body={body} 
                title={title} 
                closeTxt={'סגור'}
                makeTrigerBtn={makeTrigerBtn}
                openController={{open, }}
            />
        </div>
    );
}

export default AddNewSystemModal;
