import React, {useState} from 'react';
import Box from '@mui/material/Box';
import {    
            DataGrid, 
            heIL,
            gridPageCountSelector,
            gridPageSizeSelector,
            gridPageSelector,
            useGridApiContext,
            useGridSelector, 
            GridToolbar,
        } from '@mui/x-data-grid';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Pagination, Select, MenuItem } from '@mui/material';

const theme = createTheme(heIL);

function CustomPagination() {
    const apiRef = useGridApiContext();
    const page = useGridSelector(apiRef, gridPageSelector);
    const pageCount = useGridSelector(apiRef, gridPageCountSelector);
    const pageSize = useGridSelector(apiRef, gridPageSizeSelector);

    return (
        <div style={{direction : 'ltr', textAlign : 'center'}}>

            <div style={{display : 'inline-block', verticalAlign : 'middle', visibility : 'hidden'}}>
                _
            </div>

            <div style={{display : 'inline-block', verticalAlign : 'middle'}}>
                <Select 
                    size='small'
                    value={pageSize}
                    onChange={(e) => apiRef.current.setPageSize(e.target.value)}
                >
                    <MenuItem value={10}>10</MenuItem>
                    <MenuItem value={25}>25</MenuItem>
                    <MenuItem value={50}>50</MenuItem>
                </Select>
            </div>

            <div style={{display : 'inline-block', verticalAlign : 'middle', visibility : 'hidden'}}>
                ___
            </div>

            <div style={{display : 'inline-block', verticalAlign : 'middle'}}>
                <Pagination
                    siblingCount={0}
                    color="primary"
                    variant="outlined" 
                    count={pageCount}
                    page={page + 1}
                    onChange={(event, value) => apiRef.current.setPage(value - 1)}
                />
            </div>

        </div>
    );
}

export default function DataTable(props) {
    return (
        <Box sx={{ height: '65vh', width: '100%'}}>
            <ThemeProvider theme={theme}>
                <DataGrid
                    sx={{ backgroundColor : '#f9f9f9' }}
                    pageSize={10}
                    // autoHeight // prevent overflow
                    {...props}
                    components={{
                            Toolbar : GridToolbar,
                            Pagination: CustomPagination,
                    }}
                />
            </ThemeProvider>
        </Box>
    );
}
