import fetch from '../../lib/fetch';

const SERVER_URL = process.env.REACT_APP_SERVER_URL;

const addSystem = async (data) => {
    const url = `${SERVER_URL}/api/systems/add-system`;
    let res = null;

    try {
        const resp = await fetch.fetchPost(url, data);
        res = resp.data;
    } catch {}

    return res;
}

const getSystemsPaginated = async (page, limit, query={}) => {
    const url = `${SERVER_URL}/api/systems/get-systems`;
    let res = null;

    try {
        const resp = await fetch.fetchGet(url, {page, limit});
        res = resp.data;
    } catch {}

    return res;
}

const funcs = {
    addSystem,
    getSystemsPaginated,
}

export default funcs;
