import utils from '../../utils/system/utils';
import DataTable from '../../components/DataTable';
import React, {useState, useEffect} from 'react';
import { useNavigate } from 'react-router-dom';


function SystemsTable(props) {
    const [pageState, setPageState] = useState({
        isLoading : false,
        data : [],
        total : 0,
        page : 1,
        pageSize : 25
    });

    const navigate = useNavigate();

    const columns = [
        {
            field: 'name',
            headerName: 'שם מערכת',
            width: 150,
            renderCell : (params) => (
                <b
                    title='פתח'
                    style={{cursor : 'pointer'}}
                    onClick={() => navigate(`${params.id}`, {state : {name : params.value}})}
                >
                    {params.value}
                </b>
            )
            // editable: true,
        },
        {
            field: 'direction',
            headerName: 'סוג מערכת',
            width: 150,
            // editable: true,
        },
        {
            field: 'gitPath',
            headerName: 'שם בגיטהאב',
            width: 110,
            // editable: true,
        },
    ];

    useEffect(()=>{
        let isMounted = true;

        const getData = async ()=>{
            setPageState(old => ({...old, isLoading : true}));
            return await utils.getSystemsPaginated(pageState.page, pageState.pageSize);
        };

        getData().then(
            resp =>{
                if (isMounted) {
                    setPageState(old => ({...old, isLoading : false, data : resp.data, total : resp.total }));
                }
            }
        );
        return () => { isMounted = false };
    },[pageState.page, pageState.pageSize]);

    // const onFilterChange = useCallback((filterModel) => {
    //     console.log(filterModel);
    //     // Here you save the data you need from the filter model
    //     setQueryOptions({ filterModel: { ...filterModel } });
    // }, []);

    return (
        <DataTable
            rows={pageState.data}
            rowCount={pageState.total}
            loading={pageState.isLoading}
            page={ pageState.page -1 }
            paginationMode='server'
            columns={columns}
            pageSize={pageState.pageSize}
            onPageChange={ newPage => setPageState(old => ({ ...old, page : newPage + 1 }))}
            disableSelectionOnClick
            // filterMode="server"
            // onFilterModelChange={onFilterChange}    
            // checkboxSelection
        />
    );
}

export default SystemsTable;
