import React from 'react';
import { useFormik, FormikProvider } from 'formik';
// import MultiSelectComp from '../../selectComp/MultiSelectComp';
import { DateTimePicker } from '@mui/x-date-pickers/DateTimePicker';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { TextField, Button, MenuItem, Checkbox } from '@mui/material';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';


function FormTamplate(props) {

    const requiredFields = props.requiredFields; // list of required fields
    const initialValues = props.initialValues;
    const multySelectFlg = props.multiSelect;
    const onSubmit = props.onSubmit;
    const fields = props.fields;

    const validate = async (val)=>{
        const errors = {};
        for (let i = 0; i < requiredFields.length; i++){
            let field = requiredFields[i];
            if(!val[field]){
                errors[field] ='שדה חובה';
            }
            if(field === 'userName'){
                if ( await props.isAvailable(val[field]) === false) errors[field] ='שם משתמש תפוס';
            }
        }
        return errors;
    }


    const formik = useFormik({
        initialValues : initialValues,
        onSubmit      : onSubmit,
        validate      : validate
    });

    const createFields = (field, index, size='large') => {
        let fieldName = field.name || field._id;
        let errorCon = field.mendatory ? (formik.errors[fieldName] && formik.touched[fieldName] ? true : false) : null;
        let helperTextCon = field.mendatory ?(formik.touched[fieldName]? formik.errors[fieldName] : null) : null;
        let type = field.type || 'text';
        let res = null;

        if (type === 'select') {
            res = (
                <div key={index} style={{paddingLeft : '10px', paddingRight : '10px'}}>
                    {field.title} :
                    <TextField
                        select
                        fullWidth
                        size={size}
                        name={fieldName}
                        value={formik.values[fieldName]}
                        onChange={formik.handleChange}
                        error = {errorCon}
                        helperText={helperTextCon} 
                        {...formik.getFieldProps(fieldName)}
                    >
                        {field?.items?.map((option, index) => (
                        <MenuItem key={index} value={option?.val || option?.value} style={{direction : 'rtl'}}>
                            {option?.name || option?.title}
                        </MenuItem>
                        ))}
                    </TextField>
                </div>
            )
        } else if (type === 'checkbox'){
            res = (
                <div key={index} style={{paddingLeft : '10px', paddingRight : '10px', direction : 'ltr'}}>
                    <label>
                        {field.title}
                        <Checkbox 
                            defaultChecked={initialValues[fieldName]} 
                            onChange={formik.handleChange}
                            name={fieldName}
                        />
                    </label>
                </div>
            )
        } else if (type === "datetime"){
            res = (
                <div  key={index} style={{direction : 'rtl', paddingLeft : '10px', paddingRight : '10px'}}>
                    {field.title} : 
                    <LocalizationProvider dateAdapter={AdapterDateFns}>
                        <DateTimePicker
                            ampm={false}
                            fullWidth
                            size={size}
                            error = {errorCon}
                            name={fieldName}
                            helperText={helperTextCon} 
                            {...formik.getFieldProps(fieldName)}
                            value={formik.values[fieldName] || Date.now()}
                            onChange={val => {
                                let date = (new Date(val)).getTime();
                                return formik.setFieldValue(fieldName, date);
                            }}
                            renderInput={(params) => <TextField {...params} size="small" fullWidth/>}
                        /> 
                    </LocalizationProvider>
                </div>
            )
        } else {
            res = (
                <div key={index} style={{paddingLeft : '10px', paddingRight : '10px'}}>
                    {field.title} :
                    <TextField
                        fullWidth
                        size={size}
                        type={type}
                        name={fieldName}
                        autoComplete='off'
                        error = {errorCon}
                        helperText={helperTextCon} 
                        {...formik.getFieldProps(fieldName)}
                    />
                    { size !== 'small' ?  <><br/><br/></> : null }
                </div>
            )
        }
        return res
    }

    let formFields = fields?.map((field, index) => createFields(field, index));

    let form = (
        <FormikProvider value={formik}>
            <form onSubmit={formik.handleSubmit} style = {{'direction' : "rtl"}} >
                <div style = {{'overflow' : 'auto', 'maxHeight' : '60vh', 'padding' : '10px'}}> {/* maybe need to change */}
                    {formFields}
                    {/* {multiSelect} */}
                </div>

                <Button type="submit" fullWidth color = 'success' variant="contained">שמור</Button>
            </form>
        </FormikProvider>
    )

    return (
        <div>
            {form}
        </div>
    );
}

export default FormTamplate;
