import React, {useState} from 'react';
import { IconButton } from '@mui/material';
import generalUtils from '../../utils/utils';
import AddIcon from '@mui/icons-material/Add';
import DataTable from '../../components/DataTable';
import ChoseFileBtn from '../../components/ChoseFileBtn';

import ModalTemplate from '../../components/ModalComp';;

function LoadDataModal(props) {
    const [open, setOpen] = useState(false);
    const [rows, setRows] = useState([]);

    const title = 'טען רשומות';

    const columns = [
        {
            field: 'name',
            headerName: 'שם',
            width: 150,
            // editable: true,
        },
        {
            field: 'phone',
            headerName: 'טלפון',
            width: 150,
            // editable: true,
        },
    ];

    if (rows) {
        let temp = rows[0];
        if (temp) console.log(temp?.__rowNum__);
    }
    const handleOnFileSelected = (file) => {
        generalUtils.readExcel(file, setRows)
    };

    const makeTrigerBtn = (additionalProps) => {
        return (
            <IconButton 
                onClick={additionalProps.onClick} 
                title='טען דאטא' 
                color='info'
            >
                <AddIcon/>
            </IconButton>
        )
    }

    const body = (
        <div>
            <DataTable
                rows={rows}
                rowCount={rows?.length}
                columns={columns}
                pageSize={50}
                getRowId={(row) => row.__rowNum__}
                disableSelectionOnClick
            />
            <ChoseFileBtn title='בחר' onFileSelected={handleOnFileSelected}/>
        </div>
    )

    return (
        <div>
            <ModalTemplate 
                closeBtn 
                body={body} 
                fullWidth
                title={title} 
                closeTxt={'סגור'}
                makeTrigerBtn={makeTrigerBtn}
                openController={{open, setOpen}}
            />
        </div>
    );
}

export default LoadDataModal;
