import { useEffect, useState, useRef } from 'react';
import { register } from './utils';


function Phone(props) {
    const [ua, setUa] = useState(null);
    const [connecting, setConnecting] = useState(false);
    const [critical, setCritical] = useState(false);
    const [sipStatus, setSipStatus] = useState('');
    const [incomingCall, setIncomingCall] = useState(null);
    const [outgoingCall, setOutgoingCall] = useState(null);

    const audioRef = useRef();

    useEffect(() => {
        setConnecting(true);
        register('200-dataocean', 'RjH2TV2s9s8FHSpz', (userAgent) => {
            setUa(userAgent);
        });

        return () => {
            if (ua) {
                ua.stop();
            }
        };
        // eslint-disable-next-line
    }, []);

    useEffect(() => {
        if (ua) {
            ua.on('registered', () => {
                setConnecting(false);
                setCritical(false);
            });

            ua.on('registrationFailed', () => {
                setConnecting(false);
                setCritical(true);
            });

            ua.on('invite', (session) => {
                setIncomingCall(session);
                setSipStatus('incoming');

                session.on('accepted', () => {
                    setupRemoteMedia(session);
                    setSipStatus('accepted');
                });

                session.on('failed', () => {
                    setSipStatus('failed');
                });

                session.on('ended', () => {
                    cleanupMedia();
                    setSipStatus('ended');
                    setIncomingCall(null);
                });
            });
        }
    }, [ua]);

    const setupRemoteMedia = (session) => {
        session.connection.addEventListener('addstream', (e) => {
            let player = audioRef.current;
            player.srcObject = e.stream
            player.play();
        })
    }

    const cleanupMedia = () => {
        let player = audioRef.current;
        player.srcObject = null;
        player.pause();
    }

    const makeCall = (phone) => {
        let eventHandlers = {
            'connecting': (e) => {
                console.log('call is in progress')
                setSipStatus('connecting')
            },
            'progress': (e) => {
                console.log('call is in progress')
                setSipStatus("progress")
            },
            'failed': (e) => {
                console.log('call failed with cause: ', e)
                setSipStatus("failed")
            },
            'ended': (e) => {
                console.log('call ended with cause: ', e)
                cleanupMedia()
                setSipStatus("ended")
            },
            'confirmed': (e) => {
                setSipStatus("confirmed")
            }
        }

        let options = {
            eventHandlers: eventHandlers,
            mediaConstraints: { 'audio': true, 'video': false }
        }
        let session = ua.call('sip:0534217014@webrtc.1com.co.il', options);
        setOutgoingCall(session);
        setupRemoteMedia(session);
    }

    const answerCall = () => {
        if (incomingCall) {
            incomingCall.answer({
                mediaConstraints: { 'audio': true, 'video': false },
            });

            setSipStatus('accepted');
        }
    }

    const rejectCall = () => {
        if (incomingCall) {
            incomingCall.terminate();
            setIncomingCall(null);
            setSipStatus('rejected');
        }
    }

    return (
        <div>

            {connecting ? 'connecting' : ''}

            {critical ? 'error' : ''}

            Sip status : {sipStatus} <br />
            <button onClick={makeCall}>call</button>
            <div style={{ display: 'none' }}>
                <audio controls ref={audioRef}>
                    Your browser does not support the audio element.
                </audio>
            </div>
        </div>
    );
}

export default Phone;
