import React from 'react';
import TextError from './TextError';
import { Field, ErrorMessage } from 'formik';
import { FormGroup, Checkbox, FormControlLabel, FormControl, FormLabel, FormHelperText } from '@mui/material';

function CheckboxGroup(props) {
    const { label, name, options, ...rest } = props;
    // return (
    //     <div className='form-control'>
    //         <label>{label}</label>
    //         <Field name={name} {...rest}>
    //             {
    //                 ({ field }) => {
    //                     return options.map((option, index) => {
    //                         return(
    //                             <React.Fragment key={index}>
    //                                 <input 
    //                                     type='checkbox' 
    //                                     id={option.value} 
    //                                     {...field} 
    //                                     value={option.value} 
    //                                     checked={ field.value.includes(option.value) }
    //                                 />
    //                                 <label htmlFor={option.value}>{option.key}</label>
    //                             </React.Fragment>
    //                         )
    //                     })
    //                 }
    //             }
    //         </Field>
    //         <ErrorMessage name={name} component={TextError} />
    //     </div>
    // );

    return (
        <>
            {
                ({field, form}) => {
                    return <FormControl error={form.errors[name] && form.touched[name]}>
                        <FormLabel htmlFor={name}>{label}</FormLabel>
                        <FormGroup name={name} {...rest}>
                            {
                                options.map((option, index) => {

                                    return <Field
                                                type="checkbox"
                                                name={name}
                                                value={option.value}
                                                key={index}
                                                as={FormControlLabel}
                                                control={
                                                    <Checkbox
                                                        checked={field.value.includes(option.value)}
                                                    />
                                                }
                                                label={option.key}

                                    />
                                })
                            }
                        </FormGroup>
                        {
                            form.errors[name] && form.touched[name] ? (
                                <FormHelperText error >{form.errors[name]}</FormHelperText>
                            ) : null
                        } 
                    </FormControl>
                }
            }
        </>
    );
}

export default CheckboxGroup;
