import React, {useState} from 'react';
import {Snackbar, Button} from '@mui/material';



function IncommingCallSnackBar({call, incomingAlert}) {

    const answer = () => {
        call.accept();
    }

    const dismiss = () => {
        call.reject();
    }


    const action = (
        <>
            <Button color="success" size="small" onClick={answer}>
                מענה
            </Button>
            <Button color="error" size="small" onClick={dismiss}>
                דחייה
            </Button>
        </>
    );


    let ringtone = (
        <audio controls autoPlay loop style={{'display' : 'none'}}>
            <source src='https://finance-server.com/records/ringtone.mp3'/>
            Your browser does not support the audio element.
        </audio>
        )

    let incoming = (
        <div>
            <Snackbar
                anchorOrigin={{ vertical : 'top', horizontal : 'center' }}
                open={true}
                message={`שיחה נכנסת`}
                action={action}
            />
            {ringtone}
    </div>
    )

    return (
        <div>
            {incoming}
        </div>
    );
}

export default IncommingCallSnackBar;
