import React from 'react';
import * as Yup from 'yup';
import { Formik, Form } from 'formik';
import Button from '@mui/material/Button';
import utils from '../../utils/callCannon/callCannonUtils';
import FormikControl from '../../components/FormComp/FormikControl';


function CallCannonByDataFrom(props) {
    const initialValues = { 
        amount : '',
        endDate : null,
        lastCallDate : null,
    };

    const validationSchema = Yup.object({
        amount  : Yup.number().integer().min(1, 'הזן מספר גדול מ-0').required('שדה חובה'),
        endDate : Yup.date().transform((val, originalVal) => originalVal ? new Date(originalVal) : null).nullable(),
        lastCallDate : Yup.date().transform((val, originalVal) => originalVal ? new Date(originalVal) : null).nullable(),
    });

    const onSubmit = async (values) => {
        // let res = await utils.addSystem(values);
        let data = {
            ...values,
            did  : '123',
            name : String(new Date()),
            systemId : props.systemId,
        }
        utils.startCallCannon(data, props.gitPath);
    }

    return (
        <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            onSubmit={onSubmit}
        >
            {
                formik => <Form>
                    <FormikControl 
                        control='input' 
                        type='number'
                        label='הזן כמות דאטא שעליה המערכת תרוץ' 
                        name='amount' 
                        size='small'
                        fullWidth
                        autoComplete='off'
                        />
                    <br/><br/>
                    <FormikControl 
                        control='date' 
                        label='חייג ללידים שלא דיברנו איתם מתאריך' 
                        name='lastCallDate' 
                        size='small'
                        fullWidth
                        autoComplete='off'
                        />
                    <br/><br/>
                    <FormikControl 
                        control='date' 
                        label='הזן תאריך סיום (אופציונאלי)' 
                        name='endDate' 
                        size='small'
                        fullWidth
                        autoComplete='off'
                        />
                    <br/><br/>
                    <Button fullWidth type='submit' color='success' variant='outlined'>
                        הפעל תותח
                    </Button>
                </Form>
            }
        </Formik>
    );
}

export default CallCannonByDataFrom;
