import React, {useState} from 'react';
import generalUtils from '../../utils/utils';
import AddIcon from '@mui/icons-material/Add';
import { IconButton, Grid } from '@mui/material';
import DataTable from '../../components/DataTable';
import ChoseFileBtn from '../../components/ChoseFileBtn';

import ModalTemplate from '../../components/ModalComp';;

function LoadDataModal(props) {
    const [open, setOpen] = useState(false);
    const [pageState, setPageState] = useState({
        isLoading : false,
        data : [],
        total : 0,
    });

    const title = 'טען רשומות';

    const columns = [
        {
            field: 'name',
            headerName: 'שם',
            width: 150,
            // editable: true,
        },
        {
            field: 'phone',
            headerName: 'טלפון',
            width: 150,
            // editable: true,
        },
        {
            field: 'city',
            headerName: 'עיר',
            width: 150,
            // editable: true,
        },
        {
            field: 'birthDate',
            headerName: 'תאריך לידה',
            width: 150,
            // editable: true,
        },
        {
            field: 'email',
            headerName: 'אימייל',
            width: 150,
            // editable: true,
        },
    ];


    const handleOnFileSelected = async (file) => {
        const setItems = items => setPageState(old => ({...old, data : items, total : items.length}));

        setPageState(old => ({...old, isLoading : true}));

        await generalUtils.readExcel(file, setItems);

        setPageState(old => ({...old, isLoading : false}));
    };

    const makeTrigerBtn = (additionalProps) => {
        return (
            <IconButton 
                onClick={additionalProps.onClick} 
                title='טען דאטא' 
                color='info'
            >
                <AddIcon/>
            </IconButton>
        )
    }

    const body = (
        <div>
            <DataTable
                rows={pageState?.data}
                rowCount={pageState?.total}
                loading={pageState.isLoading}
                columns={columns}
                getRowId={(row) => row.__rowNum__}
                disableSelectionOnClick
                checkboxSelection

            />
            <br/>
            <ChoseFileBtn title='בחר רשומות' onFileSelected={handleOnFileSelected}/>
        </div>
    )

    return (
        <div>
            <ModalTemplate 
                closeBtn 
                fullWidth
                body={body} 
                title={title} 
                closeTxt={'סגור'}
                makeTrigerBtn={makeTrigerBtn}
                openController={{open, setOpen}}
                closeCallBack={() => setPageState({data: [], total : 0})}
            />
        </div>
    );
}

export default LoadDataModal;
