import React, {useState, useEffect} from 'react';
import utils from '../../../../utils/manage/recordsUtils';

function OnGoingCallsSatusView(props) {
    const [statusCount, setSatusCount] = useState();

    useEffect(()=>{
        let isMounted = true;

        const getData = async ()=>{
            return await utils.getRecordsCountByStatus(props.systemId);
        };

        getData().then(
            data =>{
                if (isMounted) {
                    setSatusCount(data)
                }
            }
        );
        return () => { isMounted = false };
    },[props.systemId]);


    return (
        <div>
            {
                statusCount ? (
                    <div>
                        מאתחל : {statusCount.initiatedCount} <br/>
                        מחייג : {statusCount.ringingCount} <br/>
                        בשיחה : {statusCount.inProgressCount} <br/>
                    </div>
                ) : <div> אין שיחות פעילות כרגע </div>
            }
        </div>
    );
}

export default OnGoingCallsSatusView;
