import utils from '../../../../utils/manage/recordsUtils';
import DataTable from '../../../../components/DataTable';
import React, {useState, useEffect} from 'react';
// import { useNavigate } from 'react-router-dom';


function ActiveCallsTable(props) {
    const [pageState, setPageState] = useState({
        isLoading : false,
        data : [],
        total : 0,
        page : 1,
        pageSize : 25
    });

    // const navigate = useNavigate();

    const columns = [
        {
            field: 'name',
            headerName: 'שם ליד',
            width: 150,
            renderCell : (params) => (
                <b
                    title='פתח'
                    style={{cursor : 'pointer'}}
                    // onClick={() => navigate(`${params.id}`, {
                    //     state : {
                    //         name    : params.value,
                    //         gitPath : params.row.gitPath
                    //     }
                    // })}
                >
                    {params.value}
                </b>
            )
        },
        {
            field: 'phone',
            headerName: 'טלפון',
            width: 150,
            // editable: true,
        },
    ];

    useEffect(()=>{
        let isMounted = true;

        const getData = async ()=>{
            setPageState(old => ({...old, isLoading : true}));
            return await utils.getRecordsInActiveCall(pageState.page, pageState.pageSize, props.systemId);
        };

        getData().then(
            resp =>{
                if (isMounted) {
                    setPageState(old => ({...old, isLoading : false, data : resp.results, total : resp.total }));
                }
            }
        );
        return () => { isMounted = false };
    },[pageState.page, pageState.pageSize, props.systemId]);


    return (
        <DataTable
            rows={pageState.data}
            rowCount={pageState.total}
            loading={pageState.isLoading}
            page={ pageState.page -1 }
            paginationMode='server'
            columns={columns}
            pageSize={pageState.pageSize}
            getRowId={(row => row._id)}
            onPageChange={ newPage => setPageState(old => ({ ...old, page : newPage + 1 }))}
            disableSelectionOnClick
        />
    );
}

export default ActiveCallsTable;
