import React from 'react';
import TextError from './TextError';
import { Field, ErrorMessage } from 'formik';
import { RadioGroup, Radio, FormControlLabel, FormControl, FormLabel, FormHelperText } from '@mui/material';

function RadioButtons(props) {
    const { label, name, options, ...rest } = props;
    return (
        <div className='form-control'>
            <label>{label}</label>
            <Field name={name} {...rest}>
                {
                    ({ field }) => {
                        return options.map((option, index) => {
                            return(
                                <React.Fragment key={index}>
                                    <input 
                                        type='radio' 
                                        id={option.value} 
                                        {...field} 
                                        value={option.value} 
                                        checked={ field.value === option.value }
                                    />
                                    <label htmlFor={option.value}>{option.key}</label>
                                </React.Fragment>
                            )
                        })
                    }
                }
            </Field>
            <ErrorMessage name={name} component={TextError} />
        </div>
    );


    return (
        <Field name={name}>
            {
                ({field, form}) => {
                    return <FormControl error={form.errors[name] && form.touched[name]}>
                        <FormLabel htmlFor={name}>{label}</FormLabel>
                        {/* <TextField id={name} {...rest} {...field} error={form.errors[name] && form.touched[name]}/> */}
                        <RadioGroup name={name}>
                            {
                                options.map((option) => {
                                    return <FormControlLabel 
                                        value={option.value} 
                                        control={<Radio />} 
                                        label={option.key} 
                                    />
                                })
                            }
                        </RadioGroup>
                        {
                            form.errors[name] && form.touched[name] ? (
                                <FormHelperText error >{form.errors[name]}</FormHelperText>
                            ) : null
                        } 
                    </FormControl>
                }
            }
        </Field>
    );
}

export default RadioButtons;
