import React, {useState} from 'react';
import { IconButton } from '@mui/material';
import generalUtils from '../../utils/utils';
import AddIcon from '@mui/icons-material/Add';
import ChoseFileBtn from '../../components/ChoseFileBtn';


import ModalTemplate from '../../components/ModalComp';;

function LoadDataModal(props) {
    const [open, setOpen] = useState(false);
    const [rows, setRows] = useState([]);

    const title = 'טען רשומות';
    if (rows) console.log(rows[0]);
    const handleOnFileSelected = (file) => {
        generalUtils.readExcel(file, setRows)
    };

    const makeTrigerBtn = (additionalProps) => {
        return (
            <IconButton 
                onClick={additionalProps.onClick} 
                title='טען דאטא' 
                color='info'
            >
                <AddIcon/>
            </IconButton>
        )
    }

    const body = (
        <div>
            <ChoseFileBtn title='בחר' onFileSelected={handleOnFileSelected}/>
        </div>
    )

    return (
        <div>
            <ModalTemplate 
                closeBtn 
                body={body} 
                title={title} 
                closeTxt={'סגור'}
                makeTrigerBtn={makeTrigerBtn}
                openController={{open, setOpen}}
            />
        </div>
    );
}

export default LoadDataModal;
