import React, { useState } from 'react';
import { Grid, Button, TextField, IconButton } from '@mui/material';
import { SessionState } from "sip.js";

import BackspaceIcon from '@mui/icons-material/Backspace';
import PhoneInTalkIcon from '@mui/icons-material/PhoneInTalk';
import CallEndSharpIcon from '@mui/icons-material/CallEndSharp';


const DialPad = ({ makeCall, call }) => {
    const [number, setNumber] = useState('');
    const [callInProgress, setCallProgress] = useState(false);

    const endCall = () => {
        switch (call?._state) {
            case SessionState.Initial:
                call.cancel();
                break;
            case SessionState.Establishing:
                call.cancel();
                break;
            case SessionState.Established:
                call.bye();
                break;
            case SessionState.Terminating:
                break;
            case SessionState.Terminated:
                break;

            default:
                break;
        }
        setCallProgress(false);
    }

    const handleNumberChange = (event) => {
        setNumber(event.target.value);
    };

    const handleCallClick = () => {
        console.log(`Calling number: ${number}`);
        setCallProgress(true);
        makeCall(number);
        // Add your logic for initiating a call here
    };

    const handleDeleteClick = () => {
        setNumber(number.slice(0, -1));
    };

    const onDigitPress = (digit) => {
        if (call?._state === SessionState.Established) {
            call.info({
                requestOptions : {
                    body: {
                        contentDisposition: "render",
                        contentType: "application/dtmf-relay",
                        content: `Signal=${digit}\r\nDuration=1000`
                    }
                }
            })
        }
        // if call is active, we will send the digit as dtmf
        setNumber(prev => `${prev}${digit}`);
    }

    const DigitBtn = ({ digit }) => (
        <Button variant="outlined" onClick={() => onDigitPress(digit)}>
            {digit}
        </Button>
    );

    const digits = ['3', '2', '1', '6', '5', '4', '9', '8', '7', '#', '0', '*'];

    return (
        <Grid container spacing={2} justify="center" style={{ maxWidth: '200px' }}>
            <Grid item xs={12}>
                <TextField
                    label="הזן מספר טלפון"
                    value={number}
                    onChange={handleNumberChange}
                    variant="standard"
                    fullWidth
                />
            </Grid>
            {
                digits.map(digit => <Grid item xs={4} key={digit}><DigitBtn digit={digit} /></Grid>)
            }
            <Grid item xs={12} style={{ textAlign: 'center' }}>
                <Grid container>
                    <Grid item xs={4}>
                        <IconButton
                            color="error"
                            onClick={endCall}
                            style={{ visibility: callInProgress && call ? 'visible' : 'hidden' }}

                        >
                            <CallEndSharpIcon />
                        </IconButton>
                    </Grid>
                    <Grid item xs={4}>
                        <IconButton
                            color="success"
                            onClick={handleCallClick}
                            style={{ visibility: !callInProgress || !call ? 'visible' : 'hidden' }}
                        >
                            <PhoneInTalkIcon />
                        </IconButton>
                    </Grid>
                    <Grid item xs={4}>
                        <IconButton
                            onClick={handleDeleteClick}
                            style={{ visibility: number === '' || call ? 'hidden' : 'visible' }}
                        >
                            <BackspaceIcon />
                        </IconButton>
                    </Grid>
                </Grid>
            </Grid>
        </Grid>
    );
};

export default DialPad;
