import React, { useState, useEffect } from 'react';
import { UA } from 'sip.js';

const SoftPhone = () => {
    const [localAudio, setLocalAudio] = useState(null);
    const [remoteAudio, setRemoteAudio] = useState(null);
    const [ua, setUa] = useState(null);
    const [session, setSession] = useState(null);

    useEffect(() => {
        // Initialize SIP.js user agent
        const ua = UA({
            uri: 'sip:alice@example.com',
            wsServers: 'ws://localhost:8080'
        });

        setUa(ua);
    }, []);

    const handleCall = () => {
        // Make a call using SIP.js
        const session = ua.invite('sip:bob@example.com', {
            media: {
                constraints: { audio: true, video: false },
                render: {
                    local: localAudio,
                    remote: remoteAudio
                }
            }
        });

        setSession(session);
    };

    const handleHangup = () => {
        // Hang up the call using SIP.js
        if (session) {
            session.terminate();
        }
    };

    return (
        <div>
            <audio ref={setLocalAudio} />
            <audio ref={setRemoteAudio} autoPlay />
            <button onClick={handleCall}>Call</button>
            <button onClick={handleHangup}>Hang up</button>
        </div>
    );
};

export default SoftPhone;
