import React, { useState } from 'react';
import JsSIP from 'jssip';

const SIPClient = () => {
    const [call, setCall] = useState(null);
    const [status, setStatus] = useState('disconnected');

    const jssipConfig = {
        uri: 'sip:200-dataocean@webrtc.1com.co.il',
        password: 'RjH2TV2s9s8FHSpz',
        sockets: [new JsSIP.WebSocketInterface('wss://webrtc.1com.co.il/ws')],
    };

    const ua = new JsSIP.UA(jssipConfig);

    const handleConnect = () => {
        ua.start();
    };

    const handleDisconnect = () => {
        ua.stop();
    };

    const handleCall = () => {
        const options = {
            mediaConstraints: { audio: true },
            pcConfig: {
                iceServers: [{ urls: 'stun:stun.l.google.com:19302' }],
            },
        };
        const uri = 'sip:0534217014@webrtc.1com.co.il';
        const session = ua.call(uri, options);
        session.on('ended', () => {
            setCall(null);
        });
        setCall(session);
    };

    const handleHangup = () => {
        call.terminate();
        setCall(null);
    };

    const handleAccept = () => {
        const options = {
            mediaConstraints: { audio: true },
            pcConfig: {
                iceServers: [{ urls: 'stun:stun.l.google.com:19302' }],
            },
        };
        call.answer(options);
    };

    ua.on('connected', () => {
        setStatus('connected');
    });

    ua.on('disconnected', () => {
        setStatus('disconnected');
    });

    ua.on('newRTCSession', (data) => {
        const { session } = data;
        if (session.direction === 'incoming') {
            setCall(session);
        }
    });

    return (
        <div>
            {status === 'disconnected' ? (
                <button onClick={handleConnect}>Connect to SIP server</button>
            ) : (
                <>
                    {call === null ? (
                        <button onClick={handleCall}>Make call</button>
                    ) : (
                        <div>
                            <button onClick={handleHangup}>Hang up</button>
                            {call.isInProgress() && (
                                <div>
                                    <p>Call in progress...</p>
                                </div>
                            )}
                            {call.isReadyToReinvite() && (
                                <div>
                                    <button onClick={handleAccept}>Accept call</button>
                                </div>
                            )}
                        </div>
                    )}
                    <button onClick={handleDisconnect}>Disconnect from SIP server</button>
                </>
            )}
        </div>
    );
};

export default SIPClient;
