import React from 'react';
import { Field } from 'formik';
import { TextField, FormControl, FormLabel, FormHelperText } from '@mui/material';

function Textarea(props) {
    const { label, name, ...rest } = props;
    return (
        <Field name={name}>
            {
                ({field, form}) => {
                    return <FormControl error={form.errors[name] ? true : false}>
                        <FormLabel htmlFor={name}>{label}</FormLabel>
                        <TextField 
                            id={name} 
                            {...rest} 
                            {...field} 
                            multiline
                            rows={4}
                        />
                        <FormHelperText 
                            error 
                            disabled={!(form.errors[name] && form.touched[name])}
                        >
                            {form.errors[name]}
                        </FormHelperText>
                    </FormControl>
                }
            }
        </Field>
    );
}

export default Textarea;
