import './style.css';
import React, {useState, useEffect} from 'react';
import utils from '../../utils/manage/recordsUtils';
import { Grid, CircularProgress } from '@mui/material';

function RecordsDataField(props) {

    const [recordsCount, setRecordsCount] = useState(null);

    useEffect(()=>{
        let isMounted = true;

        const getData = async ()=>{
            return await utils.getRecordsCount(props.systemId);
        };

        getData().then(
            data =>{
                if (isMounted) {
                    setRecordsCount(data);
                }
            }
        );
        return () => { isMounted = false };
    },[props.systemId]);

    return (
        <Grid container alignItems='center' className='field'>
            <Grid item xs={3} className='field-title'>
                מספר רשומות :
            </Grid>
            <Grid item xs={6}>
                {
                    recordsCount !== null ? 
                        recordsCount 
                        : 
                        <CircularProgress color='info' size={15}/>
                }
            </Grid>

        </Grid>
    );
}

export default RecordsDataField;
