import React, {useState, useEffect} from 'react';
import { Device } from 'twilio-client'
import utils from '../utils/phoneUtils';

function Phone(props) {
    const [identity, setIdentity] = useState('');
    const [status, setStatus] = useState('');
    const [ready, setReady] = useState(false);
    const [device, setDevice] = useState(null);
    const [callActive, setCallActice] = useState(false);
    const [call, setCall] = useState();

    useEffect(()=>{
        let isMounted = true;

        const getData = async ()=>{
            return await utils.generateToken('userName');
        };

        const interval = setInterval(() => {
            getData().then(
                data => {
                    console.log(1);
                    if (isMounted) {
                        const twilioDevice = new Device(data.token); 

                        twilioDevice.on('incoming', connection => {
                            // immediately accepts incoming connection
                            connection.accept();
                            console.log(2);
                            setStatus(connection.status());
                        });

                        twilioDevice.on('ready', device => {
                            setStatus("device ready");
                            setReady(true);
                            setDevice(device)
                        });

                        twilioDevice.on('connect', connection => {

                            if (connection.status() === 'open') {
                                setCallActice(true)
                                setCall(connection)
                            } 

                            setStatus(connection.status())
                            if ("phoneNumber" in connection.message) {
                                setStatus("In call with " + connection.message.phoneNumber);
                            }
                        });

                        twilioDevice.on('disconnect', connection => {
                            console.log(connection.status());
                            setStatus(connection.status())
                            if (connection.status() === 'closed') {
                                setCallActice(false)
                            } 
                        });

                        twilioDevice.on("error", (error) => {
                            console.log("Twilio.Device Error: " + error.message);
                            setStatus("ERROR: " + error.message);
                        }, 1000 * 60 * 50); // 50 minutes
                    }
                }
            );
        })


        return () => { 
            isMounted = false
            clearInterval(interval);
        };
    },[]);


    return (
        <div>
                    <input 
                        type="text" 
                        value={identity}
                        placeholder="phone"
                        onChange={(e) => setIdentity(e.target.value)}
                    />
                    <input 
                        disabled={ready ? false : true}
                        type="button" 
                        value="call" 
                        onClick={async () => {
                            device.connect({ phoneNumber : '972534217014' });
                        }}
                    />
                    <br/>
                    <button
                        disabled={!callActive}
                        onClick={() => device.disconnectAll()}
                    >
                        hangup
                    </button>
                    <button
                        disabled={!callActive}
                        onClick={() => call.mute(true)}
                    >
                        mute
                    </button>
                    <button
                        disabled={!callActive}
                        onClick={() => call.mute(false)}
                    >
                        unmute
                    </button>
            <p>{ status }</p>
        </div>
    );
}

export default Phone;
