import React, { useState, useEffect } from "react";
import JsSIP from "jssip";

const SIP_USERNAME = "200-dataocean";
const SIP_PASSWORD = "RjH2TV2s9s8FHSpz";
const SIP_DOMAIN = "webrtc.1com.co.il";
const SIP_SERVER = `wss://${SIP_DOMAIN}/ws`;

function Softphone() {
    const [ua, setUa] = useState(null);
    const [isRegistered, setIsRegistered] = useState(false);
    const [isInCall, setIsInCall] = useState(false);
    const [remoteStream, setRemoteStream] = useState(null);

    useEffect(() => {
        const socket = new JsSIP.WebSocketInterface(SIP_SERVER);
        const configuration = {
            sockets: [socket],
            uri: `sip:${SIP_USERNAME}@${SIP_DOMAIN}`,
            password: SIP_PASSWORD,
        };
        const userAgent = new JsSIP.UA(configuration);

        setUa(userAgent);

        userAgent.on("registered", (data) => {
            console.log("SIP registered:", data);
            setIsRegistered(true);
        });

        userAgent.on("unregistered", (data) => {
            console.log("SIP unregistered:", data);
            setIsRegistered(false);
        });

        userAgent.on("registrationFailed", (data) => {
            console.log("SIP registration failed:", data);
            setIsRegistered(false);
        });

        userAgent.on("newRTCSession", (data) => {
            const session = data.session;

            session.on("peerconnection", (data) => {
                const pc = data.peerconnection;

                const remoteStream = new MediaStream();
                pc.getReceivers().forEach((receiver) => {
                    const track = receiver.track;
                    if (track) {
                        remoteStream.addTrack(track);
                    }
                });
                setRemoteStream(remoteStream);
            });

            session.on("ended", () => {
                console.log("SIP call ended");
                setIsInCall(false);
                setRemoteStream(null);
            });

            setIsInCall(true);
        });

        userAgent.start();

        return () => {
            userAgent.stop();
        };
    }, []);

    const handleCall = () => {
        const options = {
            mediaConstraints: { audio: true, video: false },
        };
        const session = ua.call(`sip:${'534217014'}@${SIP_DOMAIN}`, options);
        console.log(session);
    };

    const handleHangup = () => {
        const activeSession = ua.getActiveSession();
        if (activeSession) {
            activeSession.terminate();
        }
    };

    return (
        <div>
            <h1>Softphone</h1>
            <div>Status: {isRegistered ? "Registered" : "Not registered"}</div>
            <br />
            {!isInCall && (
                <button disabled={!isRegistered} onClick={handleCall}>
                    Call
                </button>
            )}
            {isInCall && (
                <button disabled={!isRegistered} onClick={handleHangup}>
                    Hang up
                </button>
            )}
            {remoteStream && (
                <audio autoPlay srcObject={remoteStream}></audio>
            )}
        </div>
    );
}

export default Softphone;
