import React from 'react';
import * as Yup from 'yup';
import { Formik, Form } from 'formik';
import Button from '@mui/material/Button';
import FormikControl from '../../components/FormComp/FormikControl';


function AddNewSystemForm(props) {
    const selectSystemCallDirectionOptions = [
        {key : 'בחר', value : ''},
        {key : 'שיחות יוצאות', value : 'outbound'},
        {key : 'שיחות נכנסות', value : 'inbound'},
    ]

    const initialValues = { 
        name : '',
        gitPath : '',
        direction: '',
    };

    const validationSchema = Yup.object({
        name : Yup.string().required('שדה חובה'),
        gitPath : Yup.string().matches(/.*\.git$/, 'יש לצרף את הסיומת : ".git"').required('שדה חובה'),
        direction : Yup.string().required('שדה חובה'),

    });

    const onSubmit = values => console.log('Form data', values);

    return (
        <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            onSubmit={onSubmit}
        >
            {
                formik => <Form>
                    <FormikControl 
                        control='input' 
                        label='שם מערכת' 
                        name='name' 
                        size='small'
                        fullWidth
                        autoComplete='off'
                        />
                    <br/>
                    <FormikControl 
                        control='input' 
                        label='שם רפוזטורי בגיטהאב' 
                        name='gitPath' 
                        size='small'
                        fullWidth
                        autoComplete='off'
                        style={{direction : 'ltr'}}
                    />
                    <br/>
                    <FormikControl 
                        control='select' 
                        label='סוג מערכת שיחות' 
                        name='direction' 
                        options={selectSystemCallDirectionOptions} 
                        size='small' 
                        fullWidth
                    />

                    <Button type='submit'>שלח</Button>
                </Form>
            }
        </Formik>
    );
}

export default AddNewSystemForm;
