import React, { useRef, useState } from 'react';
import { Inviter, SessionState, UserAgent, Registerer } from "sip.js";

const WebRtcDomain = process.env.REACT_APP_WEBRTC_DOMAIN;

function PhoneHandler({ children, password, ext }) {
    const [incomingAlert, setIncomingAlert] = useState(false);
    const [call, setCall] = useState(null);

    const audioRef = useRef();

    const remoteStream = new MediaStream();

    const setupRemoteMedia = (session) => {
        session.sessionDescriptionHandler.peerConnection.getReceivers().forEach((receiver) => {
            if (receiver.track) {
                remoteStream.addTrack(receiver.track);
            }
        });

        let player = audioRef.current;
        player.srcObject = remoteStream;
        player.play();

        setIncomingAlert(false);
    }

    const cleanupMedia = () => {
        let player = audioRef.current;
        player.srcObject = null;
        player.pause();
    }

    const onInvite = (invitation) => {
        console.log({ invitation });
        setIncomingAlert(true);

        invitation.stateChange.addListener((state) => {
            console.log(`Session state changed to ${state}`);
            switch (state) {
                case SessionState.Initial:
                    break;
                case SessionState.Establishing:
                    break;
                case SessionState.Established:
                    setupRemoteMedia(invitation);
                    break;
                case SessionState.Terminating:
                // fall through
                case SessionState.Terminated:
                    cleanupMedia();
                    setCall(null);
                    break;
                default:
                    throw new Error("Unknown session state.");
            }
        });

        setCall(invitation);
        // invitation.accept();
    }

    const transportOptions = {
        server: `wss://${WebRtcDomain}/ws` // will be in .env file
    };

    const uri = UserAgent.makeURI(`sip:${ext}-dataocean@${WebRtcDomain}`);

    const userAgentOptions = {
        authorizationPassword: password,
        authorizationUsername: `${ext}-dataocean`,
        transportOptions,
        logBuiltinEnabled: false, // if true show all sip logs
        delegate: {
            onInvite
        },
        uri
    };


    const userAgent = new UserAgent(userAgentOptions);

    const registerer = new Registerer(userAgent);

    userAgent.start().then(() => {
        registerer.register();
    });

    const makeCall = (phone) => {
        const target = UserAgent.makeURI(`sip:${phone}@${WebRtcDomain}`);

        const inviter = new Inviter(userAgent, target);

        inviter.stateChange.addListener((state) => {
            console.log(`Session state changed to ${state}`);
            switch (state) {
                case SessionState.Initial:
                    break;
                case SessionState.Establishing:
                    break;
                case SessionState.Established:
                    setupRemoteMedia(inviter);
                    break;
                case SessionState.Terminating:
                // fall through
                case SessionState.Terminated:
                    cleanupMedia();
                    break;
                default:
                    throw new Error("Unknown session state.");
            }
        });

        inviter.invite();
    }


    return (
        <div>

            <div style={{ display: 'none' }}>
                <audio controls ref={audioRef}>
                    Your browser does not support the audio element.
                </audio>
            </div>
            {
                React.Children.map(children, child => {
                    return React.cloneElement(child, { makeCall, incomingAlert, call });
                })
            }
        </div>
    );
}

export default PhoneHandler;
