import fetch from '../../lib/fetch';

const SERVER_URL = process.env.REACT_APP_SERVER_URL;

const generateCallController = async (data) => {
    let res = null;

    try {
        let resp = await fetch.fetchPost(`${SERVER_URL}/api/call-cannon/careate-callController`, data);
        res = resp.data;
    } catch {}

    return res;
}

const startCallCannon = async (callController, gitPath) => {
    let res = null;
    let botsAmount = null;
console.log(callController);
    if( callController.amount ){
        try {
            botsAmount = Math.ceil( callController.amount / 100 );
        } catch {}
    } else if (callController.botsAmount) botsAmount = callController.botsAmount;

    if (botsAmount) {
        try {
            let callControllerObj = await generateCallController(callController);
            console.log(botsAmount, gitPath, callControllerObj[0]._id);
            // startCallCannon(botsAmount, gitPath, callControllerObj[0]._id)
            // calculate num of agents and send it to server, server will create them. thats it!
            let resp = await fetch.fetchPost(`${SERVER_URL}/api/call-cannon/start-call-cannon`, {
                botsAmount,
                data : { 
                    git : gitPath, 
                    systemId : callController.systemId,
                    callControllerId : callControllerObj[0]._id, 
                    sheets_is : callController.sheetsId,
                    systemName : callController.systemName,
                }
            })
            console.log(resp.data);
        } catch {}
    } else {
        console.log('error : you ask for 0 bots');
    }
    return res
}



const funcs = {
    startCallCannon,
}

export default funcs;
