import React, {useState} from 'react';
import generalUtils from '../../utils/utils';
import AddIcon from '@mui/icons-material/Add';
import { Fab, Grid, Button } from '@mui/material';
import DataTable from '../../components/DataTable';
import utils from '../../utils/manage/recordsUtils';
import ChoseFileBtn from '../../components/ChoseFileBtn';


import ModalTemplate from '../../components/ModalComp';;

function LoadDataModal(props) {
    const [open, setOpen] = useState(false);
    const [fileName, setFileName] = useState();
    const [pageState, setPageState] = useState({
        isLoading : false,
        data : [],
        total : 0,
    });

    const fabStyle = {
        margin: 0,
        top: 'auto',
        right: 'auto',
        bottom: 20,
        left: 20,
        position: 'fixed',
    };

    const title = 'טען רשומות';

    const columns = [
        {
            field: 'name',
            headerName: 'שם',
            width: 150,
            // editable: true,
        },
        {
            field: 'phone',
            headerName: 'טלפון',
            width: 150,
            // editable: true,
        },
        {
            field: 'city',
            headerName: 'עיר',
            width: 150,
            // editable: true,
        },
        {
            field: 'birthDate',
            headerName: 'תאריך לידה',
            width: 150,
            // editable: true,
        },
        {
            field: 'email',
            headerName: 'אימייל',
            width: 150,
            // editable: true,
        },
    ];


    const handleOnFileSelected = async (file) => {
        const setItems = items => setPageState(old => ({...old, data : items, total : items.length}));

        setFileName(file);
        console.log(fileName);
        setPageState(old => ({...old, isLoading : true}));

        await generalUtils.readExcel(file, setItems);

        setPageState(old => ({...old, isLoading : false}));
    };

    const makeTrigerBtn = (additionalProps) => {
        return (
            <Fab onClick={additionalProps.onClick} style={fabStyle} title='טען דאטא' color='info'>
                <AddIcon/>
            </Fab>
        )
    }

    const body = (
        <div>
            <DataTable
                rows={pageState?.data}
                rowCount={pageState?.total}
                loading={pageState.isLoading}
                columns={columns}
                getRowId={(row) => row.__rowNum__}
                disableSelectionOnClick
                checkboxSelection

            />
            <br/>
            <Grid container spacing={1}>
                {
                    pageState?.data?.length === 0 ? (
                        <Grid item xs={12}>
                            <ChoseFileBtn title='בחר רשומות' onFileSelected={handleOnFileSelected}/>
                        </Grid>
                    ) : (
                        <>
                            <Grid item xs={12} sm={6}>
                                <ChoseFileBtn title='בחר רשומה אחרת' onFileSelected={handleOnFileSelected}/>
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <Button
                                    fullWidth
                                    color='success'
                                    variant='contained'
                                    onClick={() => utils.uploadRecords(pageState.data, props.systemId)}
                                >
                                    העלה רשומות
                                </Button>
                            </Grid>
                        </>
                    )
                }
            </Grid>
        </div>
    )

    return (
        <div>
            <ModalTemplate 
                closeBtn 
                fullWidth
                body={body} 
                title={title} 
                closeTxt={'סגור'}
                makeTrigerBtn={makeTrigerBtn}
                openController={{open, setOpen}}
                closeCallBack={() => setPageState({data: [], total : 0})}
            />
        </div>
    );
}

export default LoadDataModal;
