import fetch from '../../lib/fetch';
import utils from '../utils';

const SERVER_URL = process.env.REACT_APP_SERVER_URL;


const getRecordsCount = async (systemId) => {
    const url = `${SERVER_URL}/api/records/get-records-count`;
    let res = null;

    try {
        const resp = await fetch.fetchGet(url, {systemId});
        res = resp.data;
    } catch {}

    return res;
}

const uploadRecords = async (records, systemId) => {
    let res = {status : 'success', msg : 'דאטא נטענה בהצלחה'};
    let formatedRecords = [];

    for (let index = 0; index < records.length; index++) {
        const record = records[index];

        record.systemId = systemId;

        record.phone = utils.validatePhoneNumber(record.phone);

        if (record.phone && record.name !== '') formatedRecords.push(record);

        // proccessing
        if (index !== 0 && index % 100 === 0) {
            // send formated to server
            let resp = await fetch.fetchPost(`${SERVER_URL}/api/records/add-records`, {records : formatedRecords});
            if (resp.data !== 'ok') res = {status : 'error', msg : 'חלה שגיאה'}
            // reset formated array
            formatedRecords = []
        }
    }

    // send formated that left to server
    let resp = await fetch.fetchPost(`${SERVER_URL}/api/records/add-records`, {records : formatedRecords})
    if (resp.data !== 'ok') res = {status : 'error', msg : 'חלה שגיאה'}

    console.log(res);
    return res;
}

const funcs = {
    getRecordsCount,
    uploadRecords,
}

export default funcs;
