import React, {useState} from 'react';
import {Dialog, DialogActions, DialogContent, Divider} from '@mui/material';
import {DialogContentText, DialogTitle, Container, Typography} from '@mui/material';


function ModalTemplate(props) {
    const [open, setOpen] = useState(false);

    const body = props.body;
    const title = props.title;
    const content = props.content;
    const makeTrigerBtn = props.makeTrigerBtn; // a func that get props json and create a component
    const openController = props.openController; // in some case we want to have more control on open and setOpen therefore the parent comp can send them as well
    const fullWidth = props.fullWidth || false;
    const maxWidth = props.maxWidth || "lg";

    const handleClickOpen = () => {
        if(openController){ // if we got open and set open from parent use them
            openController.setOpen(true)
        } else { // use the default
            setOpen(true);
        }
    };

    const handleClose = () => {
        if(openController){
            openController.setOpen(false)
        } else {
            setOpen(false);
        }
        if (props.closeCallBack) props.closeCallBack();
    };
    return (
        <div>
            {makeTrigerBtn ? makeTrigerBtn({onClick : handleClickOpen}) : null}
        <Dialog
            open={openController ? openController.open : open}
            style ={{'direction' : 'rtl'}}
            fullWidth={fullWidth}
            onClose={handleClose}
            maxWidth={maxWidth}
        >
            {
                title ? (
                    <DialogTitle id="alert-dialog-title">
                            <div>
                                <b style={{float : 'right',  flexGrow: 1 }}>{title}aaaaaaaa</b>
                                &nbsp;
                                <b style={{minWidth : '200px'}}>&nbsp;</b>
                                <b 
                                    title='סגור'
                                    style={{float : 'left', color : 'red', cursor  : 'pointer'}}
                                    onClick={handleClose}
                                >
                                    x
                                </b>
                            </div>
                    </DialogTitle>
                ) : null
            }
            <Divider/>
            {
                content ? (
                    <DialogContent>
                        <DialogContentText id="alert-dialog-description">
                            {content}
                        </DialogContentText>
                    </DialogContent>
                ) : null
            }

            <DialogActions> 
                <Container>
                    {body}
                </Container>
            </DialogActions>
        </Dialog>
        </div>
    );
}

export default ModalTemplate;
