import React from 'react';
import * as Yup from 'yup';
import { Formik, Form } from 'formik';
import FormikControl from './FormikControl';
// i will create new form template (this time without use formik!)

function FormikContainer(props) {
    const dropdownOptions = [
        {key : 'בחר', value : ''},
        {key : 'אופציה 1', value : '1'},
        {key : 'אופציה 2', value : '2'},
        {key : 'אופציה 3', value : '3'},
    ]

    const radioOptions = [
        {key : 'אופציה 1', value : '1'},
        {key : 'אופציה 2', value : '2'},
        {key : 'אופציה 3', value : '3'},
    ]

    const checkboxOptions = [
        {key : 'אופציה 1', value : '1'},
        {key : 'אופציה 2', value : '2'},
        {key : 'אופציה 3', value : '3'},
    ]

    const initialValues = { 
        email : '',
        description : '',
        selectOption: '',
        radioOption : '',
        checkboxOption : [],
        birthDate : null,
    };

    const validationSchema = Yup.object({
        email : Yup.string().required('Required'),
        description : Yup.string().required('Required'),
        selectOption : Yup.string().required('Required'),
        radioOption : Yup.string().required('Required'),
        checkboxOption : Yup.array().required('Required'),
        birthDate : Yup.date().required('Required').nullable(),
    });
    const onSubmit = values => console.log('Form data', values);
    return (
        <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            onSubmit={onSubmit}
        >
            {
                formik => <Form>
                    <FormikControl control='input' type='email' label='אימייל' name='email' />
                    <FormikControl control='textarea' label='תיאור' name='description' />
                    <FormikControl control='select' label='בחר אופציה' name='selectOption' options={dropdownOptions} />
                    <FormikControl control='radio' label='בחר אופציה' name='radioOption' options={radioOptions} />
                    <FormikControl control='checkbox' label='בחר אופציות' name='checkboxOption' options={checkboxOptions} />
                    <button type='שלח'>Submit</button>
                </Form>
            }
        </Formik>
    );
}

export default FormikContainer;
